mapper.select:
select * from post_mng where pst_gb = '002'  
order by pst_no desc
limit #{startIndex}, #{cntPerPage}

 SELECT * FROM cs_master as cm, cs_reply as rp
WHERE cs_no = #{cs_no}
and cm.cs_no = rp.cs_no

SELECT * FROM cs_master WHERE user_id = #{user_id}
AND cs_dt >= DATE_FORMAT(#{stt_ymd}, '%Y%m%d') AND cs_dt < DATE_ADD(DATE_FORMAT(#{end_ymd}, '%Y%m%d'),INTERVAL +1 DAY)
ORDER BY cs_no DESC LIMIT #{pageDTO.startIndex}, #{pageDTO.cntPerPage}
